import { Button } from "@mui/material";
import React from "react";
import  Typewriter  from 'typewriter-effect'

export default function LandingPage() {
    return (
        <div>
            <div id="topBarLogo" style={{width:'100%', display:'flex', alignItems:'center', justifyContent:'center'}}>
                <img src={require('../Images/possibleLogoTransparent.png')} alt="Whoop Logo" style={{height:'300px', width:'auto'}}/>
            </div>
            <div id="title" style={{fontStyle:"oblique 1deg", fontSize:'32px', WebkitTextStrokeWidth:"2px", WebkitTextStrokeColor:"white", color:'transparent', width:'100%', display:'flex', alignItems:'center', justifyContent:'center', position:'relative', bottom:'120px'}}>
                <h1>
                    <Typewriter
                        options={{
                            cursor: "",
                        }}
                            onInit={(typewriter) => {
                            typewriter.changeDelay(50).typeString("WINNER PERFORMANCE").start();
                        }}
                    />
                </h1>
            </div>
            <div id="text" style={{fontSize:'24px', width:'100%', display:'flex', alignItems:'center', justifyContent:'center', position:'relative', bottom:'75px'}}>
                <p>
                 Embark on a journey of understanding your body like never before with our integration of WHOOP data. Our platform offers a dynamic visualization of your muscle recovery, 
                 highlighting potential areas of soreness after your workouts, empowering you to train smarter.
                </p>
                <span>Features: </span>
                <ul>
                    <li><b>Muscle Recovery Map:</b> A color-coded representation of your body indicates areas that may need attention or rest.</li>
                    <li><b></b>Health Insights: With our intuitive detection system, we constantly monitor for signs of overtraining or illness â€“ keeping your well-being at the forefront.</li>
                    <li><b>Performance Metrics:</b> Dive into the specifics of your last workout with heart rate data, calories burned, and a performance score, helping you track progress and set goals.</li>
                    <li><b>Workout Reports</b>: Access a detailed breakdown of your last 10 workouts, providing insights into your training patterns and recovery needs.</li>
                </ul>
            </div>
            <div style={{fontSize:'24px', width:'100%', display:'flex', alignItems:'center', justifyContent:'center', position:'relative', bottom:'75px'}}>
                <Button variant="contained" color="primary" href="/login" style={{marginRight:'30px'}}>
                    Login
                </Button>
                <Button variant="contained" color="primary" href="/register">
                    Privacy Policy
                </Button>
            </div>
        </div>
    );
}
